import React, { useEffect, useState } from "react";
import Square from '../components/Square/Square';
import { Link } from 'react-router-dom'

// import '../styles/Payment.css'

const Checkout = () => {
    const [isLoad, setLoad] = useState(false);
    useEffect(() => {
      let sqPaymentScript = document.createElement("script");
      // sandbox: https://js.squareupsandbox.com/v2/paymentform
      // production: https://js.squareup.com/v2/paymentform
      // sqPaymentScript.src = "https://js.squareup.com/v2/paymentform";
      sqPaymentScript.src = "https://js.squareupsandbox.com/v2/paymentform";
      sqPaymentScript.type = "text/javascript";
      sqPaymentScript.async = false;
      sqPaymentScript.onload = () => {
        setLoad(true);
      };
      document.getElementsByTagName("head")[0].appendChild(sqPaymentScript);
    });

    const squarePayment = isLoad ? (
          <Square paymentForm={ window.SqPaymentForm }/>
      ) : (
        // console.log("order.js error"),
        // window.location.pathname = "/errorpage"
         null

      )


      return(
          <div className="paymentWrap site-content">
            <h2 className="paymentHead">Checkout Now</h2>

            <span className="directionsTxt">Fill the form with the following <a className="link" href="https://developer.squareup.com/docs/testing/test-values">Sandbox test credit card</a> from the test values set, and then choose Place Order.</span>
            <ul>
              <li className="directionsTxt"><span className="bold">Card Number: </span>4111 1111 1111 1111</li>
              <li className="directionsTxt"><span className="bold">MM/YY: </span>12/21 (you can provide any month and year in the future)</li>
              <li className="directionsTxt"><span className="bold">CVV: </span>111</li>
              <li className="directionsTxt"><span className="bold">Postal: </span>11111 (you can provide any postal code)</li>
            </ul>
            <span className="directionsTxt">This is one of the test cards that <span className="bold">Square</span> provides for use in the Sandbox environment.</span>
            <br></br>
            <span className="directionsTxt">When you click "Place Order", this form will create a <span className="bold">nonce(token)</span> that is sent to the server.js by using the <span className="bold">fetch API.</span></span>
            <span className="directionsTxt">Then the <a href="https://developer.squareup.com/docs/sdks" className="link">Square Node.js SDK</a> library is accessed to call the <a href="https://developer.squareup.com/docs/payments-api/take-payments" className="link">Square Payments API</a> to charge the payments source using the nonce.</span>
            {squarePayment}

          </div> 
      )

  }


  export default Checkout;
